import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Button, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import '../bloodAnimation.css';

// Blood Donation Animation Component
const BloodDonationAnimation = () => {
  const [animationStep, setAnimationStep] = useState(0);
  const [isHovering, setIsHovering] = useState(false);
  const [bloodType, setBloodType] = useState('A+');
  
  // Update animation frame
  useEffect(() => {
    const interval = setInterval(() => {
      setAnimationStep(prev => (prev + 1) % 100);
    }, 50);
    
    return () => clearInterval(interval);
  }, []);
  
  // Cycle through blood types
  useEffect(() => {
    const bloodTypes = ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'];
    const interval = setInterval(() => {
      setBloodType(bloodTypes[Math.floor(Math.random() * bloodTypes.length)]);
    }, 3000);
    
    return () => clearInterval(interval);
  }, []);
  return (
    <div className="donation-animation-container" style={{ position: 'relative', width: '100%', height: '400px', borderRadius: '12px', 
                  boxShadow: '0 4px 12px rgba(0,0,0,0.1)', overflow: 'hidden', background: '#f8f9fa' }}>
      <div className="heart-icon" style={{ position: 'absolute', top: '10px', right: '20px', zIndex: 2 }}>❤️</div>
      
      {/* Blood Bag */}
      <div className="blood-bag" style={{ 
        position: 'absolute', 
        width: '250px', 
        height: '300px', 
        top: '10%', 
        left: '50%', 
        transform: 'translateX(-50%)',
        background: '#fff',
        borderRadius: '10px',
        border: '2px solid #ddd',
        boxShadow: '0 6px 15px rgba(0,0,0,0.1)'
      }}>
        {/* Blood Level - Animated */}
        <div style={{ 
          position: 'absolute', 
          bottom: '0',
          width: '100%', 
          height: `${Math.min(70 + animationStep/4, 85)}%`,
          background: 'linear-gradient(to bottom, #ff1a1a, #cc0000)',
          transition: 'height 0.2s ease-in',
          borderBottomLeftRadius: '8px',
          borderBottomRightRadius: '8px',
        }}></div>
        
        {/* Blood Type Label */}
        <div style={{
          position: 'absolute',
          top: '10px',
          left: '50%',
          transform: 'translateX(-50%)',
          background: '#fff',
          padding: '5px 20px',
          borderRadius: '20px',
          fontWeight: 'bold',
          fontSize: '24px',
          color: '#cc0000',
          zIndex: 2,
          boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
        }}>A+</div>
      </div>
      
      {/* Blood Tube */}
      <div style={{
        position: 'absolute',
        width: '4px',
        height: '70px',
        background: '#cc0000',
        top: '10%',
        left: '50%',
        zIndex: 1
      }}></div>
        {/* Animated Blood Droplets */}
      <div className="blood-drop" style={{
        position: 'absolute',
        width: '10px',
        height: '10px',
        borderRadius: '50%',
        background: '#cc0000',
        top: `${10 + (animationStep % 10) * 6}%`,
        left: '50%',
        transform: 'translateX(-50%)',
        opacity: animationStep % 20 < 10 ? 1 : 0,
        zIndex: 1
      }}></div>
      
      {/* Heart rate line */}
      <svg style={{ position: 'absolute', bottom: '20px', width: '100%', height: '40px' }} viewBox="0 0 600 100">
        <path 
          d={`M 0,50 
              Q ${25 + animationStep}, ${50 - animationStep/2},  
                ${50 + animationStep}, ${50 + animationStep} 
              T ${100 + animationStep}, 50 
              T ${150 + animationStep}, 50 
              T ${200 + animationStep}, ${40 - animationStep/4}
              T ${250 + animationStep}, ${60 + animationStep/4}
              T ${300 + animationStep}, 50
              T ${350 + animationStep}, 50
              T ${400 + animationStep}, 50
              T ${450 + animationStep}, 50
              T 600, 50`}
          fill="none"
          stroke="#cc0000"
          strokeWidth="2"
        />
      </svg>
      
      {/* Text label */}
      <div style={{
        position: 'absolute',
        bottom: '60px',
        width: '100%',
        textAlign: 'center',
        fontWeight: 'bold',
        color: '#cc0000',
        fontSize: '18px'
      }}>Donate Blood, Save Lives</div>
    </div>
  );
};

function Home() {
  return (
    <Container className="py-5">
      <Row className="align-items-center mb-5">
        <Col lg={6} className="mb-4 mb-lg-0">
          <h1 className="display-4 fw-bold text-primary">Save Lives with Your Blood Donation</h1>
          <p className="lead my-4">
            Join our community of blood donors and help save lives. Your small action can make a huge difference for someone in need.
          </p>
          <div className="d-flex gap-3 flex-wrap">
            <Button as={Link} to="/register" variant="danger" size="lg">
              Register as Donor
            </Button>
            <Button as={Link} to="/search" variant="outline-danger" size="lg">
              Find Donors
            </Button>
          </div>
        </Col>        <Col lg={6}>
          <div className="text-center">
            <BloodDonationAnimation />
          </div>
        </Col>
      </Row>

      <Row className="mb-5">
        <Col xs={12} className="text-center mb-4">
          <h2 className="section-title">Why Donate Blood?</h2>
        </Col>
        {[
          { title: "Save Lives", icon: "❤️", text: "A single donation can save up to 3 lives" },
          { title: "Always Needed", icon: "🏥", text: "Every 2 seconds someone needs blood" },
          { title: "Quick & Easy", icon: "⏱️", text: "The donation process takes less than an hour" }
        ].map((item, idx) => (
          <Col md={4} key={idx} className="mb-4">
            <Card className="h-100 text-center p-4 donor-card">
              <Card.Body>
                <div className="display-4 mb-3">{item.icon}</div>
                <Card.Title>{item.title}</Card.Title>
                <Card.Text>{item.text}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Row>
        <Col xs={12} className="text-center">
          <h2 className="section-title">Get Started Today</h2>
          <p className="lead mb-4">
            Whether you want to donate blood or find donors, we've got you covered.
          </p>
          <div className="d-flex justify-content-center gap-3 flex-wrap">
            <Button as={Link} to="/register" variant="danger" size="lg">
              Register as Donor
            </Button>
            <Button as={Link} to="/search" variant="outline-danger" size="lg">
              Find Donors
            </Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default Home;
