import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Card, Alert } from 'react-bootstrap';
import axios from 'axios';

function Register() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    bloodGroup: '',
    city: '',
    latitude: null,
    longitude: null
  });
  
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState('');
  const [geoStatus, setGeoStatus] = useState('');
  
  const bloodGroups = ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };
  
  const getLocation = () => {
    setGeoStatus('Fetching location...');
    
    if (!navigator.geolocation) {
      setGeoStatus('Geolocation is not supported by your browser');
      return;
    }
    
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setFormData(prevData => ({
          ...prevData,
          latitude: position.coords.latitude,
          longitude: position.coords.longitude
        }));
        setGeoStatus('Location fetched successfully');
      },
      () => {
        setGeoStatus('Unable to retrieve your location');
      }
    );
  };
    const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess(false);
    
    console.log('Submitting form data:', formData);
    
    try {
      // Try with explicit URL first
      const apiUrl = 'http://localhost:5001/api/register';
      console.log('Making API request to:', apiUrl);
      
      const response = await axios.post(apiUrl, formData, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      
      console.log('API response:', response.data);
      
      setSuccess(true);
      setFormData({
        name: '',
        email: '',
        phone: '',
        bloodGroup: '',
        city: '',
        latitude: null,
        longitude: null
      });
      setGeoStatus('');
    } catch (err) {
      console.error('Registration error:', err);
      setError(err.response?.data?.error || `Error: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <Container>
      <Row className="justify-content-center">
        <Col md={8} lg={6}>
          <Card className="shadow-sm form-container">
            <Card.Body className="p-4">
              <h2 className="text-center mb-4 form-title">Register as a Blood Donor</h2>
              
              {success && (
                <Alert variant="success">
                  Registration successful! Thank you for becoming a donor.
                </Alert>
              )}
              
              {error && (
                <Alert variant="danger">
                  {error}
                </Alert>
              )}
              
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Full Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Email Address</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Phone Number</Form.Label>
                  <Form.Control
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
                
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Blood Group</Form.Label>
                      <Form.Select
                        name="bloodGroup"
                        value={formData.bloodGroup}
                        onChange={handleChange}
                        required
                      >
                        <option value="">Select Blood Group</option>
                        {bloodGroups.map(group => (
                          <option key={group} value={group}>
                            {group}
                          </option>
                        ))}
                      </Form.Select>
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>City</Form.Label>
                      <Form.Control
                        type="text"
                        name="city"
                        value={formData.city}
                        onChange={handleChange}
                        required
                      />
                    </Form.Group>
                  </Col>
                </Row>
                
                <Form.Group className="mb-4">
                  <div className="d-flex justify-content-between align-items-center">
                    <Form.Label>Location (Optional)</Form.Label>
                    <Button 
                      variant="outline-secondary" 
                      size="sm" 
                      onClick={getLocation}
                      disabled={loading}
                    >
                      Get My Location
                    </Button>
                  </div>
                  
                  {geoStatus && (
                    <Alert variant="info" className="mt-2 py-1 px-2">
                      {geoStatus}
                    </Alert>
                  )}
                  
                  {formData.latitude && formData.longitude && (
                    <div className="text-muted small mt-2">
                      Coordinates captured: {formData.latitude.toFixed(6)}, {formData.longitude.toFixed(6)}
                    </div>
                  )}
                </Form.Group>
                
                <div className="d-grid">
                  <Button 
                    variant="danger" 
                    type="submit"
                    disabled={loading}
                  >
                    {loading ? 'Registering...' : 'Register as Donor'}
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default Register;
